css:
.address p {
      font-size: 13px;
      font-weight: 900;
      text-align: end;
    }
    .from_data{
        border:3px solid black;
        width: 100%;
      display: flex;
    justify-content: space-between; 
      background: #dfd4d4ab;
      margin-bottom: 5px;
    }
    .main_section {
      width: 100%;
      display: flex;
    }

    .right_section {
      width: 45%;
      border:3px solid black;
      display: block;
      padding: 5px;
      margin: 0px 0px 0px auto;
      border-radius: 20px;
    }
    .left_section {
    border-radius: 20px;
      width: 50%;
      border: 3px solid black;
      display: block;
      padding: 5px;
    }
    .line-items {
      margin-top: 5px;
      width: 100%;
    }
    .print-format td,
    .print-format th {
      padding: 0px 0px 0px 5px !important;
    }
    .line-items th {
      border: 1px solid black;
    }

    .line-items td {
      border-left: 1px solid black;
      border-right: 1px solid black;
      border-top: none;
      border-bottom: none;
    }
    table {
      width: 100%;
    }
    h2 {
      margin: 0px !important;
    }
    #last {
      border: 1px solid black;
    }

    .customer {
      margin-top: 10px;
      padding: 10px;
      border: 1px solid black;
      border-radius: 20px;
      display: flex;
    }
    .table1 {
      width: 55%;
      border: 1px solid black;
      padding: 5px;
    }
    .table1 tr {
      border-top: 1px solid black;
    }
    .table1 thead {
      padding: 0px !important;
      background: #dfd4d4 !important;
    }



    .table1 td{
        padding: 5px 0px 5px 5px !important;
    }
    .table2 {
      width: 40%;
      border: 1px solid black;
      margin: 0px 0px 0px auto;
      padding: 5px;
    }
    .table2 tr {
      border-top: 1px solid black;
    }
    .table2 td {
      padding: 6px 0px 0px 5px !important;
    }

    #data {
      display: block;
      width: 30%;
      background: #dfd4d4 !important;
    padding: 11px 0px 14px 11px !important;
    }

    .signature {
      position: relative;
      margin-top: 20px;
    }

    .line {
      width: 30%;
      height: 1px;
      background-color: #000;
      position: absolute;
      top: 14px;
      left: 93px;
    }
