css:
.container {
    width: 100%!important; 
    margin: 0 auto;

}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;

}

.logo img {
    width: 200px;
}

.company-details {
    text-align: right;
    margin-right: 30px;
}

.header-bar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: #d3d3d3;
    border: 1px solid #000;
    padding: 5px 20px;
     width: 100%;
}

.form-no, .title, .dn-number {
    font-size: 14px;
}

.title {
    font-weight: bold;
}

.dn-number span {
    color: red;
    font-weight: bold;
}

.main-content {
    display: flex;
    justify-content: space-between;
    margin-top: 20px;
}

.left-section {
    width: 58%;
    border: 3px solid #000;
    border-radius: 10px;
    padding: 5px;
    font-size: 9px;

}
.right-section {
    width: 40%;
    border: 3px solid #000;
    border-radius: 10px;
    padding: 5px;

}


.left-section p,
.right-section p {
    margin: 5px 0;
    letter-spacing: -0.5px;
    font-size:9px;
}

.left-section strong,
.right-section strong {
    display: inline-block;
    width: 100px;
    margin-left: 4%;
    font-size: 13px;
}
.container2 {
    display: flex;
    justify-content: space-between;
    padding: 20px;
    margin-top: 5%;

}

.left-table {
    border: 2px solid #000;
    border-collapse: collapse;
    width: 80%;
}

.left-table .header2 {
    background-color: gray;
    font-weight: bold;
    text-align: center;
    color: black;
}
.left-table td {
    border: 2px solid #000;
    padding: 5px;
    text-align: left;
    font-size:12px;
    color: black;
}

.right-table {
    border: 2px solid #000;
    border-collapse: collapse;
    width: 50%;
    margin-left: 100px;
    height: 35%;
    margin-top: 7%;

}

.right-table td {
    border: 1px solid #000;
    padding: 5px;
    text-align: right;
     font-size:10px;
     color: black;

}

.right-table td.gray {
    background-color: #d3d3d3;
    border-spacing: 1px; 
    display: block;
    width: 100%;
}
.print-format td, .print-format th{
    padding: 3px !important;
}
.footer {
    padding: 20px;
    margin-top: 20px;
}
.center{
    text-align: right;
}
@media (min-width: 992px) {
    .your-selector {
        width: 100%;
    }
}
  .table-container {
            width: 100%;
            overflow-x: auto; /* Add scroll if the table is too wide */
            margin-top:5px;
        }
        table {
            width: 100%;
            border-collapse: collapse;
        }
        th, td {
            padding: 8px;
            text-align: center;
        }
        th {
            background-color: #d3d3d3;
            text-align: center;
            height: 20px; /* Adjust the height to ensure equal spacing */
            line-height: 1em; /* Adjust to ensure the text is vertically centered */
            vertical-align: middle;
        }
        td {
            vertical-align: top; /* Align text to the top */
        }
        .nowrap {
            white-space: nowrap;
            font-size: 12px;
            line-height: 15px;
        }
          .header-cell {
              background-color: #d3d3d3;
              text-align: center;
             font-weight: bold;
              color: black;
             font-size: 13px;
          }
          .print-format td, .print-format th {

                  vertical-align: 0px !important;
          }
          .print-format th {
                   color: black;
                   font-weight: bold;
          }
          td{
                font-size: 12px;
          }
           @media print {
        table {
            width: 100%;
            border-collapse: collapse;
        }

        thead {
            display: table-header-group !important;
            /* Ensures headers are repeated on each page */
        }

        tfoot {
            display: table-footer-group !important;
            /* Ensures footers are repeated on each page */
        }

        tbody {
            display: table-row-group !important;
        }

        table td div {
            page-break-inside: auto !important;
        }

 }
