css:
.logs{
    display:flex;
    justify-content:space-between!important;
}
.bill-to {
    border: 2px solid #000;
    display: flex;
    justify-content: space-between;
    padding: 0;
    margin: 0;
}
.logo2 img{
    margin-top:20px!important;
}
.print-format td, .print-format th{
    padding:0px!important;
}
.customer-details {
    width: 60%;
    padding: 10px;
}

.customer-details span {
    border-bottom: 4px double #000;
    display: inline-block;
}

.customer-details h3,
.customer-details p {
    margin: 5px 0;
}

.invoice-details {
    flex: 1;
    border-left: 2px solid #000;
}
table{
    width:100%!important;

}
.invoice-details table {
    width: 100%;
    margin: 0;
    padding: 0;
    border-collapse: collapse;
}


.invoice-details table th {
    background-color: #808080;
    font-size: 16px;
    padding: 5px;
    text-transform: uppercase;
}

.invoice-details table td.caption {
    width: 30%;
    text-align: right;
    font-weight: bold;
}

.invoice-details table td.value {
    padding: 5px;
    padding-left: 20px;
    text-align: left;
    font-weight: bold;
}

.project-details {
    border: 1px solid #000;
    margin: 10px 0 10px 0;
    border-radius: 10px;
    padding:10px;

}

.project-details table {
    width: 100%;
    border-collapse: collapse;
    font-size: 14px;
}

.project-details table .caption {
    font-weight: bold;
    padding: 5px;
}

.project-details table td:nth-of-type(1) {
    width: 15%;
}

.project-details table td:nth-of-type(2) {
    width: 50%;
}

.project-details table td:nth-of-type(3) {
    width: 15%;
    padding-right: 10px;
    text-align: right;
}

.project-details table td:nth-of-type(4) {
    width: 20%;
}

.fax {
    padding-left: 40px;
    margin: 0;
    font-size: 12px;
}
.line-items{
    width:100%!important;
}

.line-items thead tr,
.line-items thead th {
    background-color: #808080;

}

.line-items thead th,
.line-items tbody td {
    padding: 10px;
    text-align:center;
}
.thead{
    width:100%;
}
.line-items table th:nth-of-type(1),
.line-items table td:nth-of-type(1) {
    text-align: left;
    width:10%!important;
    text-align:center;
    border-radius: 10px 0 0 10px;
}

.line-items table th:nth-of-type(2),
.line-items table td:nth-of-type(2) {
    text-align: left;
    width: 60%!important;

}

.line-items table th:nth-of-type(3),
.line-items table td:nth-of-type(3) {
    text-align: right;
    text-align:center;
    width: 30%!important;
    border-radius: 0 10px 10px 0;
}

hr {
    border: 1px solid #000;
    margin: 10px 0;
}

.terms-and-total {
    display: flex;
    justify-content: space-between;
}

.terms-and-total .terms {
    width: 55%;
    font-size: 14px;
}

.terms-and-total .terms h3 {
    border-bottom: 2px solid #000;
    margin-bottom: 0;
    display: inline-block;
}


.terms-and-total .bank-details {
    text-align: left;
}

.terms-and-total .total {
    margin-top: 20px;
    width: 40%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.terms-and-total .total span {
    border-bottom: 4px double #000;
}

.terms-and-total .total .for-bsi {
    margin-bottom: 60px;
}


 @media print {
        table {
            width: 100%;
            border-collapse: collapse;
        }

        thead {
            display: table-header-group !important;
            /* Ensures headers are repeated on each page */
        }

        tfoot {
            display: table-footer-group !important;
            /* Ensures footers are repeated on each page */
        }

        tbody {
            display: table-row-group !important;
        }

        table td div {
            page-break-inside: auto !important;
        }
}
