css:
body,
html {
    font-family: Arial, sans-serif;
}

.form-container {
    margin: 0 auto;
    width: 100%;
}

.form-header {
    text-align: center;
    margin-bottom: 20px;
    border-bottom: 8px solid #000 !important;
}

.form-header h1 {
    margin: 0;
    color: #f2f2f2;
    background-color: rgb(18, 45, 80);
}

.form-header .three-section {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.form-header .three-section .logo {
    width: 20%;
}

.form-header .three-section .logo img {
    width: 100%;
}

.form-header .three-section .dis {
    width: 50%;
    border-left: 3px solid #000;
    height: 97px;
    display: flex;
    justify-content: center;
    align-items: center;
}

.form-header .three-section table {
    width: 30%;
    border-left: 3px solid #000;
    height: 98px;
}

.form-header .three-section table td,
.form-header .three-section table th {
    border: 1px solid black;
    padding: 8px;
}

.form-header .three-section .dis h2 {
    margin: 0;
}

.form-section {
    margin-bottom: 20px;
}

.form-section table {
    width: 100%;
    border-collapse: collapse;
    text-align: start;
    margin: 0 auto;
}

.form-section th,
.form-section td {
    border: 2px solid #000;
    padding: 10px;
    color:#000;
    text-align: center;
    height: 40px;
}
table th,td{
    color:#000;
}

.form-section th {
    background-color: #f2f2f2;
}
.print-format{
    padding:10px;
}
.cd {
    display: flex;
    justify-content: space-between;
    width: 100%;
    text-align: start;
}

.cd p {
    border: 1px solid #000 !important;
    padding: 15px 30px;
}

.cd .right {
    width: 40%;
}

.cd .left {
    width: 50%;
}

.bank-details {
    margin-top: 20px;
    width: 50%;
    border-collapse: collapse;
    margin: 20px auto;
}

.bank-details th,
.bank-details td {
    border: 1px solid black;
    text-align: center;
    padding: 10px;
}

.bank-details th {
    background-color: #f2f2f2;
    font-weight: bold;
    color:#000;
}

.approval-table {
    width: 70%;
    border-collapse: collapse;
    margin: 20px auto;
}

.approval-table th,
.approval-table td {
    border: 1px solid black;
    text-align: center;
    width: 133px;
    height: 40px;
    padding: 10px;
}

.approval-table th {
    background-color: #f2f2f2;
    font-weight: bold;
    color:#000;
}

@media print{
.form-header .three-section table {
    width: 30%;
    border-left: 3px solid #000;
    height: 120px;
}
.form-header .three-section .dis {
    height:120px;
}

.print-format{
    padding:1px;
}
}
