css:
.color-green{
    background-color: #3B7D23 !important;
    color: white;
}
.color-purple{
   background-color: #78206E !important;
    color: white;
}
.color-blue{
    background-color: #1172BC !important;
    color: white;
}
.color-yellow{
    background-color: #FDC20E !important;
    color: white;
}
.header{
    display: flex;
    align-items: center;
}

.header .logo{
    width: 50%;
}

.header p{
    font-size: 12px;
}

.header .logo img{
    height: 120px;
    width: 100%;
}

.address{
    border-left: 2px solid #0f4b7b;
    padding-left: 10px;
    margin-left: 10px;
}
.address .clr{
    color: #0f4b7b;
    font-weight: bold;
}



td,th{
    color: #000 ;
}

table {
    width: 100%;
}

.print-format td, .print-format th{
    padding: 4px 0 !important;
}
.b-none{
  border-bottom: none !important;  
}
.b-tr td.b-block{
    border-right: 1px solid #000 !important;
    padding-left: 40px !important;
}

#vendor-table{
    border: 1px solid #000;
}


thead th, tbody td {
    border: 1px solid black;
    padding: 8px;
    font-size: 13px;
}

#vendor-table thead th {
    width: 60%;
    height: 60px;
    font-size: 20px;
    background-color: #0f4b7b;
    color: white;
}
.highlight {
    background-color: #0f4b7b;
    color: white;
}

td.head, th.head  {
    border: none !important;
}
.qre{
    display:flex;
}
.qre .p1{
    width:50%;
}
.qre .p2{
    width:50%;
}
.f-size{
    font-size: 13px;
}

.sec-table .highlight th {
    color: #fff;
    text-align: center;
}

.sec-table  tbody tr td{
    text-align: center;
}
.desc{
    width: 45%;
}

.ten-td{
    width: 10%;
}

.twe-td{
    width: 15%;
}

#l-table{
    border: 1px solid #000;
}

#l-table thead tr th{
    color: #000;
}

#l-table tbody tr td{
    border: none !important;
}

.center{
    width: 100%;
    text-align: center;
}

#l-table tfoot tr td{
    border: 1px solid #000;
    font-size: 14px;
    text-align: center;
}

.table-foot{
    width: 20% !important;
}


.sign-area{
    width: 45%;
}

#l-table tbody tr td{
    font-size: 12px;
}

.t-table{
    margin-top: 10px;
}
.t-table tbody tr {
    height: 50px;
}

#b-tr-th{
    border-bottom: 1px solid #000;
}

@media print {
        table {
            width: 100%;
            border-collapse: collapse;
        }

        thead {
            display: table-header-group !important;
            /* Ensures headers are repeated on each page */
        }

        tfoot {
            display: table-footer-group !important;
            /* Ensures footers are repeated on each page */
        }

        tbody {
            display: table-row-group !important;
        }

        table td div {
            page-break-inside: auto !important;
        }

        #footer-html {
            position: fixed; bottom: 0; width: 100%;
        }
 }

 .main-table > tbody >tr > td {
     border: none;
 }
 .width {
     width: 33% !important;
 }
