css:
body {
    padding: 0;
    margin: 0;
}

.print-format td, .print-format th {
    padding: 0 !important;
}

.item {
    margin-top: 10px;
    margin-bottom: 10px;
}

.table {
    margin: 0 !important;
}

#main-table tbody td {
    border: 1px solid black;
}

.header img {
    width: 200px;
}

.suplier {
    margin-top: 10px;
}

p, ol {
    margin: 0 !important;
    padding: 0 !important;
}

.uppercase {
    text-transform: uppercase;
}

td {
    border: 0 !important;
    padding: 5px 4px !important;
}

.bold {
    font-weight: bold;
}

.header-rt {
    margin-bottom: 10px;
    width: 50%;
    margin-left: 56%;
    transform: translate(-50%, -38px);
}

img {
    margin-left: 80px;
}

.order {
    border: 2px solid black;
    padding: 3px;
    text-align: center;
    font-size: 15px;
    font-weight: 600;
    width: 300px;
    background: darkgray;
}

header .row {
    display: flex;
    align-items: center;
    width: 100%;
}

.header-lt {
    width: 45%;   
}

header .row .header-lt {
    width: 25%;
}

header .row .header-rt {
    width: 70%;
    margin-right: 46%;
    text-align: center;
}

header .row .header-rt .order {
    margin-top: 10px;
}

header .row .header-rt .order span {
    color: #000;
    font-size: large;
    font-weight: bold;
    background-color: #c0c0c0;
    border: 1px solid #000;
    padding: 4px 100px;
}

header img {
    width: 110px;
}

#welcome-table .row {
    display: flex;
    align-items: end;
    width: 100%;
}

#welcome-table .col-5 {
    width: 42%;
}

#welcome-table .col-7 {
    width: 53%;
}

.row {
    width: 100%;
}

.right, .left {
    width: 100%;
    border: 3.5px solid #000;
    border-radius: 10px;
    padding: 10px;
}

fieldset {
    position: relative !important;
    padding: 12px 0 0 0 !important;
}
legend {
    border: 1px solid #000 !important;
    padding: 2px 6px !important;
    position: absolute!important;
    top: -25px!important;
    border-radius: 6px!important;
    background: #fff!important;
    font-size: 14px!important;
    font-weight: bold!important;
}

.welcome-table {
    position: relative;
    margin-bottom: 0;
}


.d-flex {
    display: flex;
    margin-top: 5px;
    justify-content: space-between;
}

.invoice-data {
    border: 1px solid #000;
    border-radius: 15px;
    padding: 7px !important;
    width: 100%;
    margin-top: 5px;
}

.invoice-data tbody tr {
    width: 100%;
}

.invoice-data tbody tr td:first-child {
    width: 15%;
}

.invoice-data tbody tr .sec-td {
    width: 45%;
}

#main-table {
    border: none !important;
}

#main-table thead tr {
    width: 100%;
}

#main-table thead tr th {
    color: #000;
    padding: 2px 10px;
    text-align: center;
    text-transform: uppercase;
    border: 1px solid #000 !important;
    font-weight: bold;
}

#main-table tbody tr td {
    color: #000;
    padding: 2px 10px;
    text-align: center;
    text-transform: uppercase;
    border: 1px solid #000 !important;
    font-weight: bold;
}

.total {
    text-align-last: end;
    padding-right: 2%;
    font-size: 14px;
    margin-bottom: 5px;
    font-weight: 600;
}

.line {
    width: 35%;
    margin-bottom: 3px;
    border: 1px solid black;
    margin-left: 65%;
}

.f-th {
    width: 15%;
}

.f-th-tv {
    width: 15%;
}

.f-th-tw {
    width: 40%;
}

.f-th-noq {
    width: 7%;
}

.parti-num {
    font-size: 12px !important;
    padding: 0 !important;
}

.desc {
    padding: 0 15px !important;
    font-size: 15px;
}

.container {
    padding: 7px !important;
}

.container ol {
    margin-left: 15px !important;
}
 @media print {
        table {
            width: 100%;
            border-collapse: collapse;
        }

        thead {
            display: table-header-group !important;
            /* Ensures headers are repeated on each page */
        }

        tfoot {
            display: table-footer-group !important;
            /* Ensures footers are repeated on each page */
        }

        tbody {
            display: table-row-group !important;
        }

        table td div {
            page-break-inside: auto !important;
        }
    }
