css:
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0px;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.lines {
    background: black;
    height: 128px;
    border: 1px solid black;
}

.blue-line {
    background-color: black;
    height: 20px;
    flex-grow: 1;
    margin: 0 10px;
}

.logo-section {
    display: flex;
    align-items: center;
}

.logo-section img {
    max-height: 150px;
}

.address-section {
    display: flex;
    align-items: flex-start;
}

.address {
    text-align: center;
    font-size: 14px;
    color: black;
    padding: 5px;
    letter-spacing: -1px;
}

.address p {
    margin: 5px 0;
}

.separator {
    border-left: 2px solid #003399;
    height: auto;
    margin: 0 20px;
}

h3 {
    font-weight: 10%;
    text-align: center;
    border-top: 2px solid black;
    padding-top: 9px;
    letter-spacing: -1px;
}

.invoice-container {
    width: 100%;
    margin: 0 auto;
    padding: 0px;
}

.header-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 5px;
}

.header-table td {
    border: 1px solid #000;
    padding: 10px;
}

.customer-name {
    width: 60%;
    text-align: left;
    font-weight: bold;
    font-size: 15px;
    color: black;
}

.details-table {
    width: 100%;
    border-collapse: collapse;
    font-size: 10px;
    color: black;
    font-weight: bold;
}

.details-table td {
    border: 1px solid #000;
    padding: 2px;
    font-weight: 20px;
}

.attention {
    margin-bottom: 0px;
    font-weight: bold;
    margin-left: 20px;
}

.invoice-table {
    width: 100%;
    border-collapse: collapse;
}
.left {
    margin-left: 20px;
    color: black;
}

.invoice-table th, .invoice-table td {
    border: 1px solid #000;
    padding: 5px;
    text-align: center;
    color: black;
    font-weight: 600;
    font-size: 13px;
}

.currency {
    margin-bottom: 20px;
    font-weight: bold;
    border: 1px solid #000;
    border-top: none;
}

.bank-details {
    margin-bottom: 20px;
}

.emkan {
    margin-bottom: 70px;
    font-weight: bold;
    font-size: 15px;
}



.signature {
    text-align: left;
    font-weight: bold;
    font-size: 15px;
}
