css:
.print-format .table-bordered td, .print-format .table-bordered th{
    border: 1px solid #000;
    text-align:center;
}

.print-format td, .print-format th{
    padding: 5px !important;
}

.header {
    display: flex;
    width:100%;
    margin-bottom:5px;
    justify-content: space-between;
    align-items: center;
}
.blue-line {
    background-color: black;
    height: 20px;
    flex-grow: 1;
    margin: 0 10px;
}
p{
    border-top:3px solid black;
    padding:7px 0px;
    text-align:center;
    font-weight:bold;
}

.logo-section {
    display: flex;
    height:130px;
    padding:0px 5px;
    align-items: center;
}

.logo-section img {
    max-height: 130px;
}

.address-section {
    display: flex;
    align-items: flex-start;
}

.address {
    height:130px;
    text-align: center;
    font-size: 14px;
    color: black;
    padding: 5px;
    border-left:3px solid black;
}

.address p {
    margin: 5px 0;
}

.separator {
    border-left: 2px solid #003399;
    height: auto;
    margin: 0 20px;
}
.invoice-container {
    width: 100%;
    margin: 0 auto;
    padding: 0px;
}

.header-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 5px;
}

.header-table td {
    border: 1px solid #000;
    padding: 10px;
}

.customer-name {
    width: 60%;
    text-align: left;
    font-weight: bold;
    font-size: 15px;
    color: black;
}

.details-table {
    width: 100%;
    border-collapse: collapse;
    font-size: 10px;
    color: black;
    font-weight: bold;
}

.details-table td {
    border: 1px solid #000;
    padding: 2px;
    font-weight: 20px !important;
}

.attention {
    margin-bottom: 0px;
    font-weight: bold;
    margin-left: 20px;
}

.invoice-table {
    width: 100%;
    border-collapse: collapse;
}

.invoice-table th, .invoice-table td {
    border: 1px solid #000;
    padding: 5px;
    text-align: center;
    color: black;
    font-weight: 600;
    font-size: 13px;
}

.currency {
    margin-bottom: 20px;
    font-weight: bold;
    border: 1px solid #000;
    border-top: none;
}

.text {
    font-weight: 700;
    font-size: 15px;}
p{
    margin-top:30px!important;
}


 @media print {
        table {
            width: 100%;
            border-collapse: collapse;
        }

        thead {
            display: table-header-group !important;
            /* Ensures headers are repeated on each page */
        }

        tfoot {
            display: table-footer-group !important;
            /* Ensures footers are repeated on each page */
        }

        tbody {
            display: table-row-group !important;
        }

        table td div {
            page-break-inside: auto !important;
        }
 }
