css:
 thead {

     align-items: center;
 }


 thead img {
     max-width: 150px;
     margin-bottom:40px!important;
 }
 .title {
     text-align: center;
     position: relative;
     height: 100px;

 }
 h2{
 margin-bottom:0px!important;    
 }
 .title h3{
     margin-bottom:-13px;
     font-size: 20px;
     font-weight: bold;
     letter-spacing: -1px;

 }

 img {
     position: absolute;
     left: 0;
 }
#data{
    background:darkgrey;
    font-size:15px;
    padding:5px 30px!important;
    border-radius:5px;
    font-weight:bold;

}
#SN{
 width:30%;
 background:black;
 color:white;
}

 #bold{
     font-weight:bold;
 }
 .info {
     display: flex;
     margin-top:20px;
     padding:5px 10px !important;
     border: 2px solid black;
     border-radius: 15px;
     width : 100%;
 }

 .left-section {
     width : 60%;
     margin-left: 10px;
 }

   .right-section {
     width : 40%;
 }
 .right-section  span{
  margin-left:5px;

 }
 .info p {
     font-weight: bold;
 }

 .info span {
     font-weight: lighter;
 }

 .info b {
     position: absolute;
     bottom: 8px;
     right:10px;
     color: grey;
 }

 table {
     width: 100%;
     border-collapse: collapse;
     margin-top: 10px;
 }
 .print-format td, .print-format th{
     padding:5px!important;
     color:black;
     text-align:center;
 }
         th,
 td {
     border: 1px solid #000;
     padding: 5px;
 }

 th {

     text-align: left;
     color:black;
  font-weight:600;
 }

 .footer {
     display: flex;
     justify-content: space-between;
 }

 .signatures {
     display: flex;
     justify-content: space-between;
     padding-top: 12px;
     width: 80%;
 }

 .signatures div {
     text-align: center;
     border-bottom: 1px solid #000;
     width: 18%;
 }

 .total {
     text-align: center;
     border: 1px solid #000;
     padding: 10px 2px;
     width:14%;
     border-top:0px;
 }

 .total p {
     margin: 0;
 }

 .total p:first-child {
     font-weight: bold;
     background:darkgrey;

 }
 @media print {
 table {
     width: 100%;
     border-collapse: collapse;
 }

 thead {
     display: table-header-group !important;
     /* Ensures headers are repeated on each page */
 }

 tfoot {
     display: table-footer-group !important;
     /* Ensures footers are repeated on each page */
 }

 tbody {
     display: table-row-group !important;
 }

 table td div {
     page-break-inside: auto !important;
 }
 }
