css:
* {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
        }

        body {
            margin: 5px 15px;
        }

        header {
            display: flex;
            align-items: center;
        }

        header img {
            max-width: 150px;
            margin-left: 30px;
        }

        .title {
            flex: 1;
            text-align: center;
        }

        .title h1,
        .title h2 {
            margin: 0;
        }

        .title h1 {
            font-size: 1.2em;
            margin-bottom: 10px;
            font-family:sans-serif;
        }

        .title h2 {
            font-size: 1.1em;
            background: #ccc;
            width:50%;
            padding: 5px 10px;
            display: inline-block

        }
        #bold{
            font-weight:bold;
        }
        .info {
            display: flex;
            border: 2px solid black;
            border-radius: 15px;
            width : 100%;
        }

        .left-section {
            width : 60%;
            margin-left: 10px;
        }

          .right-section {
            width : 40%;
        }
        .right-section  span{
         margin-left:5px;

        }
        .info p {
            font-weight: bold;
        }

        .info span {
            font-weight: lighter;
        }

        .info b {
            position: absolute;
            bottom: 8px;
            right:10px;
            color: grey;
        }

        table {
            width: 100%;
            border-collapse: collapse;
            margin-top: 10px;
        }
        .print-format td, .print-format th{
            padding:5px!important;
            color:black;
            text-align:center;
        }
                th,
        td {
            border: 1px solid #000;
            padding: 5px;
        }

        th {

            text-align: left;
            color:black;
         font-weight:600;
        }

        .footer {
            display: flex;
            justify-content: space-between;
        }

        .signatures {
            display: flex;
            justify-content: space-between;
            padding-top: 15px;
            margin-left: 70px;
            width: 70%;
        }

        .signatures div {
            text-align: center;
            border-bottom: 1px solid #000;
            width: 18%;
        }

        .total {
            text-align: center;
            border: 1px solid #000;
            padding: 10px 2px;
            width:14%;
        }

        .total p {
            margin: 0;
        }

        .total p:first-child {
            font-weight: bold;
            background:darkgrey;

        }

        @media print {
            body {
                margin: 0;
            }

            .info {
                padding: 10px;
            }

            .signatures div {
                padding-top: 30px;
            }

            .total {
                padding: 10px 9px;
            }
        }
